import React, { Component } from 'react';
import { ReactDOM } from 'react';
import { Prompt } from 'react-router-dom';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
// Departure Airport, Arrival Airport, Departure Date, Arrival Date, Adults, Children, Cabin class
export default class SearchPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            departureAirport: '',
            arrivalAirport: '',
            depratureDate:new Date(),
            arrivalDate:new Date(),
            adults: 0,
            children:0,
            cabinclass:''
        }
        
    }
    onChangeDepAirport(e) {
      this.setState({
        departureAirport: e.target.value
      })
     }
   
     onChangeArrAirport(e) {
       this.setState({
        arrivalAirport: e.target.value
       })
     }
   
     onChangeDepDate(e) {
       this.setState({
        depratureDate: date
       })
     }
   
     onChangeArrDate(date) {
       this.setState({
        arrivalDate: date
       })
     }
   
   
     onChangeAdults(e) {
       this.setState({
        adults: e.target.value
       })
     }
   
     onChangeChildren(e) {
       this.setState({
        children: e.target.value
       })
     }
   
     onChangeCabinClass(e){
       this.setState({
        cabinclass:e.target.value
       })
     }
    
     onSubmit(e) {
      const Search = {
        departureAirport : this.state.departureAirport,
        arrivalAirport : this.state.arrivalAirport,
        depratureDate : this.state.depratureDate,
        arrivalDate : this.state.arrivalDate,
        adults : this.state.adults,
        children : this.state.children,
        cabinclass : this.state.cabinclass,
      }
      axios.post('http://localhost:5000/guest/depFlights',Search)
      .then(result => console.log(result))
      .catch(function (error) {
        console.log(error);
      })
      alert('A Search was submitted CONGRATS ' );
      e.preventDefault();
    }
     render() {
      return (
      <div>
        <h3>Search</h3>
        <form onSubmit={this.onSubmit}>
  
          <div className="form-group"> 
            <label>Departure Airport: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.departureAirport}
                onChange={this.onChangeDepAirport}
                /> 
                </div>
   
            
         
          <div className="form-group"> 
          <label> Arrival Airport: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.departureAirport}
                onChange={this.onChangeDepAirport}
                /> 
          </div>
  
          <div className="form-group">
          <label> Departure Date: </label>
          <div>
            <DatePicker
              selected={this.state.depratureDate}
              onChange={this.onChangeDepDate}
            />
          </div>
          </div>
          <div className="form-group"> 
            <label>Arrival Date: </label>
            <DatePicker
              selected={this.state.arrivalDate}
              onChange={this.onChangeArrDate}
            />
          </div>
          
          <div className="form-group"> 
            <label>Adults: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.departureAirport}
                onChange={this.onChangeDepAirport}
                /> 
          </div>
          
          <div className="form-group"> 
            <label>Children: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.departureAirport}
                onChange={this.onChangeDepAirport}
                /> 
          </div>
          <div className="form-group"> 
            <label>Cabin Class: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.departureAirport}
                onChange={this.onChangeDepAirport}
                /> 
          </div>
          
  
          <div className="form-group">
            <input type="submit" value="Search" className="btn btn-primary" />
    
          </div>
          </form>
          
    </div>
    
  
    
        
      
      )
    }
 
  }
