import React, { Component } from 'react';
import { ReactDOM } from 'react';
import { Prompt } from 'react-router-dom';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
// Departure Airport, Arrival Airport, Departure Date, Arrival Date, Adults, Children, Cabin class
export default class SearchPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            departureAirport: '',
            arrivalAirport: '',
            depratureDate:new Date(),
            arrivalDate:new Date(),
            adults: 0,
            children:0,
            cabinclass:''
        }
        
    }
    componentDidMount() {
        axios.get('http://localhost:5000/guest/depFlights')
          .then(response => {
            this.setState({
                departureAirport:respone.data.departureAirport,
                arrivalAirport:respone.data.arrivalAirport,
                depratureDate:response.data.departureDate,
                adults: response.data.adults,
                children: response.data.children,
                cabinclass: response.data.cabinclass
            })   
          })
          .catch(function (error) {
            console.log(error);
          })

         
     }
    render() {
      return (
        <nav className="navbar navbar-dark bg-dark navbar-expand-lg">
          <Link to="/" className="navbar-brand">    Available Flights</Link>
          <div className="collpase navbar-collapse">
          <ul className="navbar-nav mr-auto">
            <li className="navbar-item">
            <Link to="/flightlist" className="nav-link">Flight List</Link>
            </li>
            <li className="navbar-item">
            <Link to="/flighttable" className="nav-link">Flight Table</Link>
            </li> 
            <li className="navbar-item">
            <Link to="/createflights" className="nav-link">Create flights</Link>
            </li>
            { <li className="navbar-item">
            <Link to="/createuser" className="nav-link">SIGN UP</Link>
            </li> }
          </ul>
          </div>
        </nav>
      );
    }
  }
