import React, { Component } from "react";
//import {Form} from "reactstrap";

import axios from 'axios';
import Step1 from "./Step1";
import Step2 from "./Step2";
import Step3 from "./Step3";

import StepProgressBar from "react-step-progress";
// import the stylesheet
import "react-step-progress/dist/index.css";

import MultiStepProgressBar from "./MultiStepProgressBar";




// import { FormGroup, Label, Input } from "reactstrap";
//import { FormGroup } from '@mui/material';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Button, CardActionArea, CardActions } from '@mui/material';

import plane from '@mui/material/plane'

class MasterForm extends Component {
  constructor(props) {
    super(props);

    // Set the intiial input values
    this.state = {
      currentStep: 1,
      flights: [],
      selected_flight: {}
    };

    // Bind the submission to handleChange()
    this.handleSelect = this.handleSelect.bind(this);

    // Bind new functions for next and previous
    this._next = this._next.bind(this);
    this._prev = this._prev.bind(this);
  }

  // Use the submitted data to set the state
  handleSelect(value) {
    this.setState({
      selected_flight : value
    });
  }
  componentDidMount() {
    axios.get('http://localhost:5000/flights/')
      .then(response => {
        this.setState({ flights: response.data })
      })
      .catch((error) => {
        console.log(error);
      });
      console.log("Getting flights Done!!")
      console.log(this.state.flights);
  }


  // Trigger an alert on form submission
  handleSubmit = event => {
    event.preventDefault();
    const { selected_flight} = this.state;
    alert(`Your Departure Flight detail: \n 
      Your Flight Number: ${selected_flight.flight_number}`);
  };

  // Test current step with ternary
  // _next and _previous functions will be called on button click
  _next() {
    let currentStep = this.state.currentStep;

    // If the current step is 1 or 2, then add one on "next" button click
    currentStep = currentStep >= 2 ? 3 : currentStep + 1;
    this.setState({
      currentStep: currentStep
    });
  }

  _prev() {
    let currentStep = this.state.currentStep;
    // If the current step is 2 or 3, then subtract one on "previous" button click
    currentStep = currentStep <= 1 ? 1 : currentStep - 1;
    this.setState({
      currentStep: currentStep
    });
  }

  // The "next" and "previous" button functions
  get previousButton() {
    let currentStep = this.state.currentStep;

    // If the current step is not 1, then render the "previous" button
    if (currentStep !== 1) {
      return (
        <Button variant="contained" onClick={this._prev}>
          Previous
        </Button>
      );
    }

    // ...else return nothing
    return null;
  }

  get nextButton() {
    let currentStep = this.state.currentStep;
    // If the current step is not 3, then render the "next" button
    if (currentStep < 3) {
      return (
        <Button variant="contained" onClick={this._next}>
          Next
        </Button>
      );
    }
    // ...else render nothing
    return null;
  }

  get submitButton() {
    let currentStep = this.state.currentStep;

    // If the current step is the last step, then render the "submit" button
    if (currentStep > 2) {
      return <Button variant="contained">Submit</Button>;
    }
    // ...else render nothing
    return null;
  }


  render() {
    return (
      <>
        <form onSubmit={this.handleSubmit}>
          <Card>

            <CardActionArea>
             <CardContent>

            <Typography gutterBottom variant="h5" component="div">
            Choose your departure and return flight
          </Typography>

                <MultiStepProgressBar currentStep={this.state.currentStep} />


                  <>
                    <p>Please select preferred departure time</p>
                    <form>
                {
                    this.state.flights.map(currentflight =>(

                    <Card sx={{ maxWidth: 345 }}>
                    <CardActionArea>
                      <CardMedia
                        component="img"
                        height="140"
                        image="../imgs/plane.jpg"
                        alt="green iguana"
                      />
                      <CardContent>
                        <Typography gutterBottom variant="h5" component="div">
                          Flight
                        </Typography>
                        <input className="e-input" type="text" placeholder="Flight Number:" value={currentflight.flight_number } readOnly= {true}/>
                        <input className="e-input" type="text" placeholder="Departure:" value={currentflight.departure} readOnly= {true}/>
                        <input className="e-input" type="text" placeholder="Economy seats:" value={currentflight.number_of_Economy_seats} readOnly= {true}/>
                        <input className="e-input" type="text" placeholder="Business class seats:" value={currentflight.number_of_Business_class_seats} readOnly= {true}/>
                      </CardContent>
                        <CardContent>
                          {this.previousButton}
                          {this.nextButton}
                          {this.submitButton}
                    </CardContent>
                    </CardActionArea>
                    <CardActions>
                      <Button size="small" variant="contained"onClick={()=>{this.setState({ selected_flight : currentflight})}}>
                        Select
                      </Button>
                    </CardActions>
                  </Card>
    ))
                }
                    </form>
                  </>


              <Step2
                currentStep={this.state.currentStep}


              />
              <Step3
                currentStep={this.state.currentStep}

              />
              </CardContent>
            </CardActionArea>

            <CardActions>
            {this.previousButton}
            {this.nextButton}
            {this.submitButton}
            </CardActions>

          </Card>
        </form>
      </>
    );
  }
}

export default MasterForm;


