import React from "react";
import { FormGroup, Label, Input } from "reactstrap";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Button, CardActionArea, CardActions } from '@mui/material';




const Step1 = props => {

  if (props.currentStep !== 1) {
    console.log("Step 1");
    return null;
  }

  return <>

  </>
};

export default Step1;


props.flights.map(currentflight =>{
  return(
  <>
    <p>Please select preferred departure time</p>
    <FormGroup>

    <Card sx={{ maxWidth: 345 }}>
    <CardActionArea>
      <CardMedia
        component="img"
        height="140"
        image="../imgs/plane.jpg"
        alt="green iguana"
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          Flight
        </Typography>



        <input className="e-input" type="text" placeholder="Flight Number:" value={currentflight.flight_number } readOnly= {true}/>
        <input className="e-input" type="text" placeholder="Departure:" value={currentflight.departure} readOnly= {true}/>
        <input className="e-input" type="text" placeholder="Economy seats:" value={currentflight.number_of_Economy_seats} readOnly= {true}/>
        <input className="e-input" type="text" placeholder="Business class seats:" value={currentflight.number_of_Business_class_seats} readOnly= {true}/>


      </CardContent>
    </CardActionArea>
    <CardActions>
      <Button size="small" color="primary" onClick={props.handleSelect(currentflight)}>
        Select
      </Button>
    </CardActions>
  </Card>

    </FormGroup>
  </>
  );
}
);
