  render() {
    return (
      <>
        <FormGroup onSubmit={this.handleSubmit}>
          <Card>
            <Typography gutterBottom variant="h5" component="div">
            Choose your departure and return flight
          </Typography>
            <CardActionArea>

                <MultiStepProgressBar currentStep={this.state.currentStep} />


                  <>
                    <p>Please select preferred departure time</p>
                    <FormGroup>
                {
                    this.state.flights.map(currentflight =>{

                    <Card sx={{ maxWidth: 345 }}>
                    <CardActionArea>
                      <CardMedia
                        component="img"
                        height="140"
                        image="../imgs/plane.jpg"
                        alt="green iguana"
                      />
                      <CardContent>
                        <Typography gutterBottom variant="h5" component="div">
                          Flight
                        </Typography>
                        <input className="e-input" type="text" placeholder="Flight Number:" value={currentflight.flight_number } readOnly= {true}/>
                        <input className="e-input" type="text" placeholder="Departure:" value={currentflight.departure} readOnly= {true}/>
                        <input className="e-input" type="text" placeholder="Economy seats:" value={currentflight.number_of_Economy_seats} readOnly= {true}/>
                        <input className="e-input" type="text" placeholder="Business class seats:" value={currentflight.number_of_Business_class_seats} readOnly= {true}/>
                      </CardContent>
                        <CardContent>
                          {this.previousButton}
                          {this.nextButton}
                          {this.submitButton}
                    </CardContent>
                    </CardActionArea>
                    <CardActions>
                      <Button size="small" color="primary" onClick={()=>{this.setState({ selected_flight : currentflight})}}>
                        Select
                      </Button>
                    </CardActions>
                  </Card>
                  })
                }
                    </FormGroup>
                  </>




              <Step2
                currentStep={this.state.currentStep}


              />
              <Step3
                currentStep={this.state.currentStep}

              />
            </CardActionArea>

            <CardActions>
            {this.previousButton}
